def test_unload(score):
	subprocess.run(["/bin/bash", "./unload.sh"])
	s = subprocess.run(["ls", "/dev/simple_character_device"],stdout=subprocess.PIPE)
	if s.returncode == 0:
		print("rm from /dev/ failed: -5")
		score -= 5
	if str(s).find('my_driver') != -1:
		print("rmmod failed: -5")
		score -= 5
	return score

